    private void SaveData(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SaveData
       
        if (jLargo.getText().equals("") || jAlto.getText().equals("") || jAncho.getText().equals("") ) {
            jLabel1.setText("Rellena todos los campos");
        }else {
             DatosEmbalajes d=new DatosEmbalajes();
             d.alto=Integer.parseInt(jAlto.getText());
             d.largo=Integer.parseInt(jLargo.getText());
             d.ancho=Integer.parseInt(jAncho.getText());
             d.EscribirFichero();
             jLabel1.setText("Añadido correctamente");
             jAlto.setText("");
             jLargo.setText("");
             jAncho.setText("");
             jLabel1.setForeground(Color.GREEN);
             
        }
          
    }//GEN-LAST:event_SaveData

    private void jAnchoKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jAnchoKeyPressed
        
        int key=evt.getKeyCode();
        if ((key>=evt.VK_0 && key<=evt.VK_9) || key==evt.VK_BACK_SPACE) {
           jAncho.setEditable(true);
           jAncho.setBackground(Color.GREEN); 

        }else {
            jAncho.setEditable(false);
            jAncho.setBackground(Color.red);
            jLabel1.setText("Introduce sólo números");
        }
    }//GEN-LAST:event_jAnchoKeyPressed

