    private void jAltoKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jAltoKeyPressed
        int key=evt.getKeyCode();
        if ((key>=evt.VK_0 && key<=evt.VK_9) || key==evt.VK_BACK_SPACE) {
           jAlto.setEditable(true);
           jAlto.setBackground(Color.GREEN); 
           jLabel1.setText("");
        }else {
            jAlto.setEditable(false);
            jAlto.setBackground(Color.red);
            jLabel1.setText("Introduce sólo números");
        }
    }//GEN-LAST:event_jAltoKeyPressed

