    private void jAnchoKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jAnchoKeyReleased
           int vl=1;
           try {
           vl=Integer.parseInt(jAncho.getText());
           }catch (Exception e){
               
           }
            
           if(vl>0 && vl<=30){
               jAncho.setBackground(Color.GREEN);
               jLabel1.setText("");
           }else {  
               jAncho.setBackground(Color.red);
               jLabel1.setText("El límite es 30");
           }       
    }//GEN-LAST:event_jAnchoKeyReleased

